&lt;li&gt;&lt;p&gt;&amp;#x51E0;&amp;#x4E4E;&amp;#x6BCF;&amp;#x4E2A;&amp;#x6D4F;&amp;#x89C8;&amp;#x5668;&amp;#x90FD;&amp;#x53EF;&amp;#x4EE5;&amp;#x901A;&amp;#x8FC7;&amp;#x68C0;&amp;#x67E5;&amp;#x5143;&amp;#x7D20;&amp;#x7684;&amp;#x65B9;&amp;#x6CD5;&amp;#x76F4;&amp;#x63A5;&amp;#x590D;&amp;#x5236;xpath&amp;#x3002;&amp;#x4F8B;&amp;#x5982;Microsoft Edge&amp;#x4E2D;&amp;#x6309;&amp;#x5FEB;&amp;#x6377;&amp;#x952E;&lt;code&gt;F12&lt;/code&gt;&amp;#x5373;&amp;#x53EF;&amp;#x8FDB;&amp;#x5165;&amp;#x5F00;&amp;#x53D1;&amp;#x8005;&amp;#x5DE5;&amp;#x5177;&amp;#x7BB1;&amp;#x3002;&amp;#x5728;&amp;#x5BF9;&amp;#x5E94;&amp;#x7684;&amp;#x6E90;&amp;#x7801;&amp;#x4F4D;&amp;#x503C;&amp;#x53F3;&amp;#x952E;&amp;#x70B9;&amp;#x51FB;&amp;#x5373;&amp;#x53EF;&amp;#x627E;&amp;#x5230;&lt;code&gt;&amp;#x590D;&amp;#x5236;&lt;/code&gt;-&amp;gt;&lt;code&gt;&amp;#x590D;&amp;#x5236;xpath&lt;/code&gt;&amp;#x9009;&amp;#x9879;&amp;#x3002;&amp;#x4E0D;&amp;#x591F;&amp;#xFF0C;&amp;#x5F53;&amp;#x4F60;&amp;#x4F7F;&amp;#x7528;&lt;code&gt;requests&lt;/code&gt;&amp;#x53BB;&lt;code&gt;get&lt;/code&gt;&amp;#x4E00;&amp;#x4E2A;&amp;#x9875;&amp;#x9762;&amp;#x7684;&amp;#x65F6;&amp;#x5019;&amp;#xFF0C;&amp;#x83B7;&amp;#x5F97;&amp;#x7684;&amp;#x9875;&amp;#x9762;&amp;#x4EE3;&amp;#x7801;&amp;#x548C;&amp;#x5F00;&amp;#x53D1;&amp;#x8005;&amp;#x5DE5;&amp;#x5177;&amp;#x4E2D;&amp;#x663E;&amp;#x793A;&amp;#x7684;&amp;#x6E90;&amp;#x7801;&amp;#x6709;&amp;#x90A3;&amp;#x4E48;&amp;#x4E00;&amp;#x70B9;&amp;#x70B9;&amp;#x533A;&amp;#x522B;&amp;#x3002;&amp;#x7F51;&amp;#x4E0A;&amp;#x53EF;&amp;#x4EE5;&amp;#x627E;&amp;#x5230;&amp;#x5F88;&amp;#x591A;&amp;#x6709;&amp;#x5173;&amp;#x8FD9;&amp;#x4E24;&amp;#x5904;&amp;#x4EE3;&amp;#x7801;&amp;#x533A;&amp;#x522B;&amp;#x7684;&amp;#x4FE1;&amp;#x606F;&amp;#x3002;&lt;/p&gt;
